import React, { useState, useRef } from "react";
import emailjs from "@emailjs/browser";

export default function ContactForm() {
  const [loading, setLoading] = useState(false);
  const formRef = useRef();

  const onSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);

    emailjs
      .sendForm(
        "",
        "YOUR_TEMPLATE_ID",
        formRef.current,
        "YOUR_PUBLIC_KEY"
      )
      .then(
        (result) => {
          console.log("Email sent:", result.text);
          alert("Message sent successfully!");
          formRef.current.reset();
        },
        (error) => {
          console.error("Email sending failed:", error.text);
          alert("Failed to send message. Try again.");
        }
      )
      .finally(() => setLoading(false));
  };

  return (
    <form ref={formRef} onSubmit={onSubmit} className="p-6 bg-white rounded-lg shadow-lg">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {/* Name */}
        <div className="form-group">
          <label className="form-label">Your Name</label>
          <input
            name="user_name"
            placeholder="Name *"
            className="form-control border p-2 w-full rounded-md"
            type="text"
            required
          />
        </div>

        {/* Email */}
        <div className="form-group">
          <label className="form-label">Your Email</label>
          <input
            name="user_email"
            placeholder="Email *"
            className="form-control border p-2 w-full rounded-md"
            type="email"
            required
          />
        </div>

        {/* Subject */}
        <div className="col-span-2 form-group">
          <label className="form-label">Subject</label>
          <input
            name="subject"
            placeholder="Subject *"
            className="form-control border p-2 w-full rounded-md"
            type="text"
            required
          />
        </div>

        {/* Message */}
        <div className="col-span-2 form-group">
          <label className="form-label">Your Message</label>
          <textarea
            name="message"
            placeholder="Your message *"
            rows="4"
            className="form-control border p-2 w-full rounded-md"
            required
          ></textarea>
        </div>

        {/* Submit Button */}
        <div className="col-span-2">
          <button
            className="bg-blue-500 text-white px-4 py-2 rounded-md w-full transition hover:bg-blue-600"
            type="submit"
            disabled={loading}
          >
            {loading ? "Sending..." : "Send Message"}
          </button>
        </div>
      </div>
    </form>
  );
}
